import React, { useEffect, useState } from 'react'

const Reference = () => {
    const[name,SetName]= useState("");

    useEffect()
  return (
    <div>
        <div>Reference</div>
        <input value={name} onChange={(e)=>SetName(e.target.validity)}/>
        <div>Number of times component got rendred.</div>
    </div>
  )
}

export default Reference
