import React, { useEffect, useRef, useState } from 'react'


// I want to presist the value between multiple
// re-renders calls without using state hook
const Reference = () => {
    const[name,SetName]= useState("");
    // const[count,setCount]=useState(0);
    let count1=useRef(0);
    console.log(count1);
    const[showMessage,setShowMessage]=useState(false);

    useEffect(()=>{
        // setCount((prevCount)=> prevCount+1);
        count1=count1.current++;
        
    },[name,showMessage]);
  return (
    <div>
        <div>Reference</div>
        <input value={name} onChange={(e)=>SetName(e.target.value)}/>
        <div>Number of times component got rendred {count1.current}.</div>
        <button onClick={()=>{setShowMessage(!showMessage)}}>Submit</button>
        {showMessage &&<div>Show message is true</div> }
    </div>
  )
}

export default Reference
